package Assignments;

public class ForLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//factorial of 5
		int num=5;
		int fact =1;
		
		for (int i=1;i<=num;i++) {
			fact = fact *i;
		}
		System.out.println(fact);
		
		
		//palindrome number
		
	}
	
	

}
